print(sum((l1 := sorted(list([int(x) for x in open("input.txt", "r").read().replace("   ", "\n").split("\n")])[::2], reverse=True))[x] * (l2 := sorted(list([int(x) for x in open("input.txt", "r").read().replace("   ", "\n").split("\n")])[1::2], reverse=True)).count(l1[x]) for x in range(int(len([int(x) for x in open("input.txt", "r").read().replace("   ", "\n").split("\n")]) / 2))))